import { createBrowserRouter, RouterProvider } from "react-router-dom";
import HomePage from "./pages/HomePage";
import EventDetailPage, {
  loader as EventDetailLoader,
} from "./pages/EventDetailPage";
import EventsPage, { loader as eventsLoader } from "./pages/EventsPage";
import NewEventPage from "./pages/NewEventPage";
import EditEventPage from "./pages/EditEventPage";
import RootLayout from "./pages/RootLayout";
import EventsRoot from "./pages/EventsRoot";
import Error from "./pages/Error";

function App() {
  const router = createBrowserRouter([
    {
      path: "/",
      element: <RootLayout />,
      errorElement: <Error />,
      children: [
        { index: true, element: <HomePage /> },
        {
          path: "events",
          element: <EventsRoot />,
          children: [
            {
              index: true,
              element: <EventsPage />, //fetching data before the component gets rendered
              //cannot use useLoaderData on a higher level element than the one where fetching data
              // move the code in the component that's needed
              loader: eventsLoader,
            },

            // #nested routes / make a children array
            {
              path: ":eventId",
              loader: EventDetailLoader,
              children: [{ path: "/edit", element: <EditEventPage /> }],
            },

            {
              path: ":eventId",
              element: <EventDetailPage />,
              // moved loader in the parent
              // loader: EventDetailLoader,
            },
            { path: "new", element: <NewEventPage /> },
          ],
        },
      ],
    },
  ]);

  return <RouterProvider router={router} />;
}

export default App;
